def create_svg_file(filepath, content):
    os.makedirs(os.path.dirname(filepath), exist_ok=True)
    with open(filepath, "w") as f:
        f.write(content)


if __name__ == "__main__":
    create_svg_file("assets/svgs/music_note.svg", SVG_MUSIC_NOTE_CONTENT)
    create_svg_file("assets/svgs/person.svg", SVG_PERSON_CONTENT)
    create_svg_file("assets/svgs/disco.svg", SVG_DISCO_CONTENT)
    create_svg_file("assets/svgs/image.svg", SVG_IMAGE_CONTENT)

    print("Arquivos SVG criados com sucesso em assets/svgs/") 